import React, { useEffect, useState } from "react";
import { useRecoilState } from "recoil";
import { fiveDaysState, cityState } from "../recoil/atom/cityAtom";
import { Card, ListGroup, ListGroupItem } from "react-bootstrap";
import { Row, Container, Col } from "react-bootstrap";
import { Link } from "react-router-dom";
import FetchDataFromAPI from "../components/FetchDataFromAPI";
const DisplayData = (props) => {
  const [fiveDays, setFiveDays] = useRecoilState(fiveDaysState);
  const [city, setCity] = useRecoilState(cityState);

  return (
    <Container>
      <h3>You Choose {city} City</h3>
      <Row>
        <FetchDataFromAPI />
        {fiveDays.map((data) => {
          let obj = [];
          let link = "";
          let main = 0;
          let temperature = 0;
          let date;
          let readableDate;
          let feelLike;
          if (data.length == 2) {
            console.log("data == 2");
          } else {
            obj = data.weather[0];
            main = obj.main;
            temperature = data.main.temp - 273.15;
            temperature = Math.round(temperature);
            feelLike = data.main.feels_like - 273.15;
            feelLike = Math.round(feelLike);
            link = `http://openweathermap.org/img/wn/${obj.icon}@2x.png`;
            data = new Date(data.dt_txt);
            readableDate = data.toDateString();
          }
          return (
            <Col key={Math.random()}>
              {" "}
              <Card className=" rounded">
                <Card.Img variant="top" src={link} />
                <Card.Body>
                  <Card.Title>
                    {main}
                    <span> {temperature}°</span>
                  </Card.Title>
                  <Card.Title>dsdas</Card.Title>
                  <Card.Title></Card.Title>
                </Card.Body>
                <ListGroup>
                  <ListGroupItem>{obj.description}</ListGroupItem>
                  <ListGroupItem>{readableDate}</ListGroupItem>
                  <ListGroupItem>Temperature: {temperature}°</ListGroupItem>
                  <ListGroupItem>Feels like : {feelLike}°</ListGroupItem>
                </ListGroup>
              </Card>
            </Col>
          );
        })}
      </Row>
    </Container>
  );
};
export default DisplayData;
