import React, { useState, useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";
import FetchDataFromAPI from "../components/FetchDataFromAPI";
import { useRecoilState } from "recoil";
import { searchWeatherState } from "../recoil/atom/cityAtom";
import { Card, ListGroup, ListGroupItem, Button } from "react-bootstrap";

import {
  cityState,
  fiveDaysState,
  weatherState,
  searchedCityState,
  locationWetherState,
  lOneDayState,
  sOneDayState,
} from "../recoil/atom/cityAtom";

const Index = () => {
  const [cityInput, setCityInput] = useState("London");
  const [searchedCity, setSearchedCity] = useRecoilState(searchedCityState);

  const [serchedCityWeather, setserchedCityWeather] =
    useRecoilState(searchWeatherState);

  const [lWeather, setLWeather] = useRecoilState(locationWetherState);
  const [lOneDay, setLOneDay] = useRecoilState(lOneDayState);
  const [sOneDay, setSOneDay] = useRecoilState(sOneDayState);

  const [fiveDays, setFiveDays] = useRecoilState(fiveDaysState);

  const handelChange = (e) => {
    setCityInput(e.target.value);
  };
  const handelClick = () => {
    setSearchedCity(cityInput);
  };
  const link = `http://openweathermap.org/img/wn/${lOneDay.weather[0].icon}@2x.png`;

  const form = (_) => {
    return (
      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Enter Ther City Name"
          aria-label="Recipient's username"
          aria-describedby="button-addon2"
          onChange={handelChange}
        />
        <button
          onClick={handelClick}
          className="btn btn-outline-secondary"
          type="button"
          id="button-addon2"
        >
          Search
        </button>
      </div>
    );
  };
  return (
    <Container>
      {form()}
      <Row>
        <FetchDataFromAPI />
        <Col>
          <Card style={{ width: "18rem", marginLeft: "40px" }}>
            <Card.Img variant="top" src="holder.js/100px180" />
            <Card.Body>
              <Card.Title>Card Title</Card.Title>
              <Card.Text>
                Some quick example text to build on the card title and make up
                the bulk of the card's content.
              </Card.Text>
              <Button variant="primary">Go somewhere</Button>
            </Card.Body>
          </Card>
        </Col>
        <Col>
          <Card style={{ width: "18rem", marginLeft: "180px" }}>
            <Card.Img variant="top" src="holder.js/100px180" />
            <Card.Body>
              <Card.Title>Card Title</Card.Title>
              <Card.Text>
                Some quick example text to build on the card title and make up
                the bulk of the card's content.
              </Card.Text>
              <Button variant="primary">Go somewhere</Button>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Index;
