import React, { useEffect, useState } from "react";
import { useRecoilState } from "recoil";
import { fiveDaysState, cityState } from "../recoil/atom/cityAtom";
import { Card, ListGroup, ListGroupItem } from "react-bootstrap";
import { Row, Container, Col } from "react-bootstrap";
import { Link } from "react-router-dom";
import FetchDataFromAPI from "../components/FetchDataFromAPI";
import FiveDays from "./FiveDays";
import Index from "./Index";
const DisplayData = (props) => {
  const [fiveDays, setFiveDays] = useRecoilState(fiveDaysState);
  const [city, setCity] = useRecoilState(cityState);
  let icon = [];

  useEffect(() => {
    if (fiveDays.length == 0 || fiveDays.length == 2) {
      return console.log("no Data");
    } else {
      fiveDays.map((data) => {
        console.log(data.weather[0].icon);
        const link = `http://openweathermap.org/img/wn/${data.weather[0].icon}@2x.png`;
        icon.push(link);
        let x = (data.main.temp - 273.15) * 10;
        x = Math.round(x);
        let temp = data.main.temp - 273.15;
        temp = Math.round(temp);
        x = x / 10;
        let y = (data.main.feels_like - 273.15) * 10;
        y = Math.round(y);

        y = y / 10;

        let date = new Date(data.dt_txt);
        let readableDate = date.toDateString();

        let month = date.getMonth() + 1;
        let day = date.getDate() + "-" + month;
        return <p>SDASDASDASDAS</p>;
      });
    }
  }, [fiveDays]);
  return (
    <div>
      <FetchDataFromAPI />
      {fiveDays.map((f) => {
        return <div>{f.dt}</div>;
      })}
      Display Data
      {fiveDays.length}
    </div>
  );
};
export default DisplayData;
