import { atom } from "recoil";

export const cityState = atom({
  key: "cityName",
  default: "gaza",
});
export const weatherState = atom({
  key: "weather",
  default: "recoil",
});
export const searchedState = atom({
  key: "searched",
  default: "recoilSearched",
});
export const locatedState = atom({
  key: "locatedState",
  default: "recoilLocated",
});

export const fiveDaysState = atom({
  key: "fiveDaysState",
  default: [],
});
