import React, { useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import FetchDataFromAPI from "../components/FetchDataFromAPI";
import { useRecoilState } from "recoil";
import { Card, ListGroup, ListGroupItem } from "react-bootstrap";

import {
  cityState,
  fiveDaysState,
  weatherState,
} from "../recoil/atom/cityAtom";
const [serchedCity, setSerchedCity] = useState("London");

const handelChange = (e) => {
  setSerchedCity(e.target.value);
};

const handelClick = (e) => {};
const Index = () => {
  const [fiveDays, setFiveDays] = useRecoilState(fiveDaysState);

  return (
    <Container>
      <div class="input-group mb-3">
        <input
          type="text"
          class="form-control"
          placeholder="Enter Ther City Name"
          aria-label="Recipient's username"
          aria-describedby="button-addon2"
          onChange={handelChange}
        />
        <button
          onClick={handelClick}
          class="btn btn-outline-secondary"
          type="button"
          id="button-addon2"
        >
          Search
        </button>
      </div>

      <FetchDataFromAPI />
      <Card className="my-3 p-3 rounded">
        <Card.Img variant="top" />
        <Card.Body>
          <Card.Title>
            {" "}
            <span>°</span>
          </Card.Title>
          <Card.Title></Card.Title>
          <Card.Title></Card.Title>
        </Card.Body>
        <ListGroup className="list-group-flush">
          <ListGroupItem></ListGroupItem>
          <ListGroupItem></ListGroupItem>
          <ListGroupItem>Temperature: °</ListGroupItem>
          <ListGroupItem>Feels like : °</ListGroupItem>
        </ListGroup>
      </Card>
    </Container>
  );
};

export default Index;
