import React, { useState } from "react";
import { useEffect } from "react";
import axios from "axios";
import { useRecoilState } from "recoil";
import {
  cityState,
  fiveDaysState,
  weatherState,
  searchedState,
  searchedCityState,
} from "../recoil/atom/cityAtom";
import { Col, Row, Container } from "react-bootstrap";
import GetLocation from "./GetLocation";
import Index from "../screens/Index";

const key = "478f1611be4e3a210abc53f2b1c9e8d4";
const FetchDataFromAPI = () => {
  const [weather, setWeather] = useRecoilState(weatherState);
  const [searchedCity, setSearchedCity] = useRecoilState(searchedCityState);

  const [serchedCityWeather, setserchedCityWeather] =
    useRecoilState(searchedState);

  const [city, setCity] = useRecoilState(cityState);
  const [fiveDays, setFiveDays] = useRecoilState(fiveDaysState);
  // const [ss, setSS] = useState();

  const link = `https://api.openweathermap.org/data/2.5/forecast?q=${city}&appid=${key}`;
  const searchedLink = `https://api.openweathermap.org/data/2.5/forecast?q=${searchedCity}&appid=${key}`;

  ////////////////////Fetch Data

  const sendReq = async () => {
    if (
      window.location.pathname == "" ||
      (window.location.pathname == "/" && searchedCity != "")
    ) {
      // const serchedCityReq = await axios.get(searchedLink);
      // setserchedCityWeather(serchedCityReq);
      // const weatherData = await axios.get(link);
      // setWeather(weatherData);
      console.log("Tow req sent");
      console.log(searchedCity);
    } else if (window.location.pathname == "/DisplayFiveDays") {
      const weatherData = await axios.get(link);
      setWeather(weatherData);
    }
  };

  const fetchData = () => {
    if (typeof city == "undefined" || city == "") {
      console.log("no city selected");
    } else {
      sendReq();
      let tempArr = [];
      if (weather == "recoil" || typeof weather == undefined) {
        console.log("City Nane  not loaded yet ");
      } else {
        let iterator = weather.data.list;

        for (let i = 0; i < iterator.length; i += 8) {
          tempArr.push(iterator[i]);
        }
        setFiveDays(tempArr);
      }
    }
  };

  useEffect(() => {
    fetchData();
  }, [city]);

  return (
    <Container>
      <GetLocation />
      <Row>
        {/* {fiveDays.map((data) => (
          <Col key={data.dt_txt}>
            <DisplayData data={data} />
          </Col>
        ))} */}
      </Row>
    </Container>
  );
};

export default FetchDataFromAPI;
