      {fiveDays.length == 2 ? (
        console.log("No Data")
      ) : (
        <Row>
          {fiveDays.map((data) => (
            <Col key={Math.random()}>
              <DisplayData data={data} />
              <div>dsadass</div>
            </Col>
          ))}
        </Row>
      )}
    </div>
  );
};
export default DisplayData;
