import { atom } from "recoil";

export const cityState = atom({
  key: "cityName",
  default: "gaza",
});
export const weatherState = atom({
  key: "weather",
  default: "recoil",
});

export const searchedCityState = atom({
  key: "searchedCity",
  default: "",
});

export const searchedState = atom({
  key: "searched",
  default: "recoilSearched",
});

export const locationState = atom({
  key: "locationState",
  default: "",
});

export const fiveDaysState = atom({
  key: "fiveDaysState",
  default: [],
});
